DROP TABLE IF EXISTS `tp5_article`;
CREATE TABLE `tp5_article` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `pid` int(11) NOT NULL DEFAULT '0' COMMENT '文章所在栏目',
  `title` varchar(255) NOT NULL,
  `description` varchar(255) NOT NULL,
  `keyword` varchar(255) NOT NULL,
  `article` longtext NOT NULL COMMENT '文章内容',
  `status` int(11) NOT NULL DEFAULT '1',
  `listorder` char(4) NOT NULL DEFAULT '0',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=10 DEFAULT CHARSET=utf32;

